import React, { useEffect } from 'react';

function Navbar({ setActiveSection }) {
    useEffect(() => {
        const buttons = document.querySelectorAll('.btn-6');

        buttons.forEach(button => {
            button.addEventListener('mouseenter', (e) => {
                const span = button.querySelector('span');
                const rect = button.getBoundingClientRect();
                const relX = e.clientX - rect.left;
                const relY = e.clientY - rect.top;
                span.style.top = `${relY}px`;
                span.style.left = `${relX}px`;
            });

            button.addEventListener('mouseout', (e) => {
                const span = button.querySelector('span');
                const rect = button.getBoundingClientRect();
                const relX = e.clientX - rect.left;
                const relY = e.clientY - rect.top;
                span.style.top = `${relY}px`;
                span.style.left = `${relX}px`;
            });
        });

        return () => {
            buttons.forEach(button => {
                button.removeEventListener('mouseenter', () => {});
                button.removeEventListener('mouseout', () => {});
            });
        };
    }, []);

    return (
        <header style={{ backgroundColor: '#333', color: 'white', padding: '10px', textAlign: 'center' }}>
            <h1>Marketplace</h1>
            <nav>
                <button className="btn-6" onClick={() => setActiveSection('home')}><span>Home</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('login')}><span>Login</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('register')}><span>Register</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('products')}><span>Products</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('auction')}><span>Auction</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('cart')}><span>Cart</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('orders')}><span>Orders</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('notifications')}><span>Notifications</span></button> |
                <button className="btn-6" onClick={() => setActiveSection('review')}><span>Review</span></button>
            </nav>
        </header>
    );
}

export default Navbar;
