import React, { useState, useEffect } from 'react';

function Review({ loggedInUserId }) {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        if (loggedInUserId) {
            // Fetch reviews for the logged-in user
            fetch(`http://localhost:5000/show-review/${loggedInUserId}`)
                .then(res => res.json())
                .then(data => {
                    console.log('Reviews fetched:', data);
                    if (Array.isArray(data)) {
                        setReviews(data);
                    } else {
                        console.error('Unexpected response format:', data);
                    }
                })
                .catch(err => {
                    console.error('Error fetching reviews:', err);
                });
        }
    }, [loggedInUserId]);

    return (
        <div>
            <h2>User Reviews</h2>
            {reviews.length === 0 ? (
                <p>No reviews available.</p>
            ) : (
                <ul>
                    {reviews.map(review => (
                        <li key={review.Review_ID}>
                            <p>Product: {review.Product_Title}</p>
                            <p>Rating: {review.Rating ? review.Rating : 'Not yet rated'}</p>
                            <p>Review: {review.Review_Text ? review.Review_Text : 'No review text provided'}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default Review;
