import React, { useState, useEffect } from 'react';

function Notifications({ loggedInUserId }) {
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        if (loggedInUserId) {
            console.log('Logged in User ID:', loggedInUserId); // Verify that user ID is available
            fetch(`http://localhost:5000/orders/${loggedInUserId}`)
                .then(res => res.json())
                .then(data => {
                    console.log('Notifications fetched:', data);
                    setNotifications(data);
                })
                .catch(err => {
                    console.error('Error fetching notifications:', err);
                });
        } else {
            console.log('No loggedInUserId found');
        }
    }, [loggedInUserId]);

    return (
        <div>
            <h2>Your Notifications</h2>
            {notifications.length > 0 ? (
                notifications.map(notification => (
                    <p key={notification.notification_id}>
                        {notification.message} - {new Date(notification.notification_Date).toLocaleString()}
                    </p>
                ))
            ) : (
                <p>No notifications at this time.</p>
            )}
        </div>
    );
}

export default Notifications;
