import React from 'react';
import styled from 'styled-components';

const Button = styled.button`
  background-color: ${(props) => theme[props.theme].default};
  color: white;
  padding: 5px 15px;
  border-radius: 5px;
  outline: 0;
  border: 0; 
  text-transform: uppercase;
  margin: 10px 0px;
  cursor: pointer;
  box-shadow: 0px 2px 2px lightgray;
  transition: ease background-color 250ms;
  &:hover {
    background-color: ${(props) => theme[props.theme].hover};
  }
  &:disabled {
    cursor: default;
    opacity: 0.7;
  }
`;

const theme = {
  light: {
    default: '#333',
    hover: '#555',
  },
  dark: {
    default: '#666',
    hover: '#888',
  },
};

function Navbar({ setActiveSection }) {
    return (
        <header style={{ backgroundColor: '#333', color: 'white', padding: '10px', textAlign: 'center' }}>
            <h1>Marketplace</h1>
            <nav>
                <Button theme="light" onClick={() => setActiveSection('home')}>Home</Button> |
                <Button theme="light" onClick={() => setActiveSection('login')}>Login</Button> |
                <Button theme="light" onClick={() => setActiveSection('register')}>Register</Button> |
                <Button theme="light" onClick={() => setActiveSection('products')}>Products</Button> |
                <Button theme="light" onClick={() => setActiveSection('auction')}>Auction</Button> |
                <Button theme="light" onClick={() => setActiveSection('cart')}>Cart</Button> |
                <Button theme="light" onClick={() => setActiveSection('orders')}>Orders</Button> |
                <Button theme="light" onClick={() => setActiveSection('notifications')}>Notifications</Button> |
                <Button theme="light" onClick={() => setActiveSection('review')}>Review</Button>
            </nav>
        </header>
    );
}

export default Navbar;
